mapper.insert:
insert into
board(bno, id, title, content)
values(
seq_bno.nextval
,#{id}
,#{title}
,#{content}
)

mapper.update:
update board
set
title = #{title}
,content = #{content}
,updateDate = sysdate
where bno =
#{bno}

update board
set views = views+1
where bno =#{bno}

mapper.select:
select * from ( select /*+index_desc(board pk_board)*/ rownum rn, bno, title, id, views, regdate, updatedate from board where
0< bno and 0<rownum and rownum<=(#{pageNum}*#{amount})
order by views desc, regdate desc, updatedate
order by regdate desc, views desc, updatedate
order by updatedate desc, views desc, regdate
) where rn>(#{pageNum}-1)*#{amount}
